const table1 = document.querySelector(`#table1`) as HTMLElement;
const table2 = document.querySelector(`#table2`) as HTMLElement;
const table3 = document.querySelector(`#table3`) as HTMLElement;
const table4 = document.querySelector(`#table4`) as HTMLElement;
const tables = document.querySelectorAll(`.table`);
const root = document.querySelector(`#root`) as HTMLElement;


class Dishe {
    constructor(public name: string, public price: number, public image: string, public description: string) {
    }
}

const dishes: Dishe[] = [];

const Meal1 = new Dishe(
    'burger',
    12.99,
    'data:image/jpeg;base64,',
    'Hamburger bun, avocado, lettuce, tomato and pickle.'
);
dishes.push(Meal1);
const Meal2 = new Dishe(
    'pizza',
    19.99,
    'data:image/jpeg;base64,',
    'Tomato sauce, cheese, olives.'
);
dishes.push(Meal2);


for (const element of tables) {

    element.addEventListener("click", ChooseADish);
    function ChooseADish() {

        element.style.background = "red";
        renderMenu(dishes, root);
    }
}



function renderMenu(
    dishes: Dishe[],
    root: HTMLElement | null) {
    try {
        const html =
            `<div class="main">
        <div class="dish">
        ${dishes.map(dishe => {
                return `<div class="dish">
                    <button onClick="Remove()"><img id="img" src="${dishe.image}" width="150px" alt="${dishe.name}" 
                    <div class="dish-text"></button> 
                    <h3>${dishe.name}</h3>
                    <p>${dishe.description}</p>
                    <p>${dishe.price}$</p>
                    </div>
                </div>`
            })};
        </div> 
    </div>`;
        if (!root) throw new Error("no root element");

        root.innerHTML += html;
    } catch (error) {
        console.error(error);
    }
}
